const TypeCheck = require('js-typecheck');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');


/**
 * Any value input.
 *
 * This input type holds any kind of object, however it's not exposed for
 * handlers ({@link Handler}). It's intended to be used only internally.
 *
 * ```javascript
 * const input = Input.create('myInput: any');
 * input.setValue({a: 1});
 * ```
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * hidden | boolean telling if the input is hidden from the {@link Reader}, \
 * therefore it should only be used internally | ::on:: | ::true::
 * allowedInstance | Specific object type that should be allowed by the input \
 * | ::off:: | ::none::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class Any extends Input{

  /**
   * Implements input's validations
   *
   * @param {null|number} at - index used when input has been created as a vector that
   * tells which value should be used
   * @return {Promise<*>} value held by the input based on the current context (at)
   * @protected
   */
  _validation(at){

    // calling super class validations
    return super._validation(at).then((value) => {

      // type checking
      if (!TypeCheck.isObject(value)){
        throw new ValidationFail(
          'Value needs to be an object!',
          '25c9158a-30ee-4a9f-8767-bc2c170f77fd',
        );
      }
      // allowed instance checking
      else if (this.property('allowedInstance') && !(value instanceof this.property('allowedInstance'))){
        throw new ValidationFail(
          `Invalid object type: ${value.constructor.name}, expecting ${this.property('allowedInstance').name}`,
          'd59814e4-0432-435a-b116-4491819c58d4',
        );
      }

      return value;
    });
  }

  /**
   * Serialization is not supported
   *
   * @return {boolean}
   */
  isSerializable(){
    return false;
  }
}

// registering the input
Input.register(Any);

// registering properties
Input.registerProperty(Any, 'hidden', true);
Input.registerProperty(Any, 'allowedInstance');

module.exports = Any;

const TypeCheck = require('js-typecheck');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');


/**
 * Base text input class derived by all text input implementations.
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * primitive | ensures the value is a primitive | ::on:: | ::true::
 * regex | custom regular expression to test the value | ::off:: | ::none::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class BaseText extends Input{

  /**
   * Returns if the input is empty
   * @return {boolean}
   */
  isEmpty(){
    return (super.isEmpty() || this.value().length === 0);
  }

  /**
   * Implements input's validations
   *
   * @param {null|number} at - index used when input has been created as a vector that
   * tells which value should be used
   * @return {Promise<*>} value held by the input based on the current context (at)
   * @protected
   */
  _validation(at){

    // calling super class validations
    return super._validation(at).then((value) => {

      // type checking
      if (!TypeCheck.isString(value)){
        throw new ValidationFail(
          'Value needs to be a string',
          '71b205ae-95ed-42a2-b5e9-ccf8e42ba454',
        );
      }
      // primitive property
      else if (this.property('primitive') && value instanceof String){
        throw new ValidationFail(
          'Value needs to be a primitive',
          '81b44982-3c7b-4a25-952b-70ec640c58d4',
        );
      }
      // regex property
      else if (this.property('regex') && (!(new RegExp(this.property('regex'))).test(value))){
        throw new ValidationFail(
          'Value does not meet the requirements',
          'c902610c-ef17-4a10-bc75-887d1550793a',
        );
      }

      return value;
    });
  }
}

// registering properties
Input.registerProperty(BaseText, 'primitive', true);
Input.registerProperty(BaseText, 'regex');

module.exports = BaseText;

const TypeCheck = require('js-typecheck');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');


/**
 * Boolean input.
 *
 * ```javascript
 * const input = Input.create('myInput: bool');
 * input.setValue(false);
 * ```
 *
 * ```javascript
 * // vector version
 * const input = Input.create('myInput: bool[]');
 * input.setValue([false, true, false]);
 * ```
 *
 * *This input can also be created using the alias:* `boolean`
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * primitive | ensures the value is a primitive | ::on:: | ::true::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class Bool extends Input{

  /**
   * Implements input's validations
   *
   * @param {null|number} at - index used when input has been created as a vector that
   * tells which value should be used
   * @return {Promise<*>} value held by the input based on the current context (at)
   * @protected
   */
  _validation(at){

    // calling super class validations
    return super._validation(at).then((value) => {

      // type checking
      if (!(TypeCheck.isBool(value) || value instanceof Boolean)){
        throw new ValidationFail(
          'Value needs to be a boolean',
          '4304c51a-a48f-41d2-a2b8-9ba43c6617f3',
        );
      }
      // primitive property
      else if (this.property('primitive') && value instanceof Boolean){
        throw new ValidationFail(
          'Value needs to be a primitive',
          '5decf593-f5a2-4368-a675-9b47256c395a',
        );
      }

      return value;
    });
  }

  /**
   * Decodes the input value from the string representation ({@link _encode}) to the
   * data type of the input. This method is called internally during {@link parseValue}
   *
   * @param {string} value - string containing the encoded value
   * @return {bool}
   * @protected
   */
  static _decode(value){
    return value.toLowerCase() === 'true' || value === '1';
  }

  /**
   * Encodes the input value to a string representation that can be later decoded
   * through {@link _decode}. This method is called internally during the
   * {@link serializeValue}
   *
   * @param {*} value - value that should be encoded to a string
   * @return {string}
   * @protected
   */
  static _encode(value){
    return (value) ? '1' : '0';
  }
}

// registering the input
Input.register(Bool);

// also, registering as 'boolean' for convenience
Input.register(Bool, 'boolean');

// registering properties
Input.registerProperty(Bool, 'primitive', true);

module.exports = Bool;

