const crypto = require('crypto');
const TypeCheck = require('js-typecheck');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');
const Hex = require('./Hex');


/**
 * Hash input.
 *
 * ```javascript
 * const input = Input.create('myInput: hash', {size: 64});
 * input.setValue('b542f74248124c32');
 * ```
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * size | bit size of the hash. This property needs to be defined at constructor \
 *  time | ::true:: | ::none::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class Hash extends Hex{

  constructor(...args){
    super(...args);

    // making sure 'size' property has been defined
    if (TypeCheck.isNone(this.property('size'))){
      throw new Error(`Can't create a hash input (${this.name()}) without the 'size' property!`);
    }

    // locking the 'size' property
    this.lockProperty('size');
  }

  /**
   * Generates a new random hash and assigns it to the value of the input. This method
   * is not supported by vector inputs.
   */
  setRandom(){
    if (this.isVector()){
      throw new Error('Not supported, input is a vector!');
    }

    const size = this.property('size', 0) / 8;
    this.setValue(crypto.randomBytes(size).toString('hex'));
  }

  /**
   * Implements input's validations
   *
   * @param {null|number} at - index used when input has been created as a vector that
   * tells which value should be used
   * @return {Promise<*>} value held by the input based on the current context (at)
   * @protected
   */
  _validation(at){

    // calling super class validations
    return super._validation(at).then((value) => {

      // size checking
      if (this.property('size') !== (value.length / 2) * 8){
        throw new ValidationFail(
          'Invalid hash size',
          'c83d98b0-8409-47c3-89c9-f81452c910c0',
        );
      }

      return value;
    });
  }
}

// registering the input
Input.register(Hash);

module.exports = Hash;

const assert = require('assert');
const TypeCheck = require('js-typecheck');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');
const BaseText = require('./BaseText');


/**
 * Hexadecimal input.
 *
 * ```javascript
 * const input = Input.create('myInput: hex');
 * input.setValue('ffff00');
 * ```
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * size | optional maximum bit size of the value | ::none:: | ::none::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class Hex extends BaseText{

  /**
   * Returns the current value in decimal
   *
   * @return {number}
   */
  decimalValue(){
    const currentValue = this.value();
    assert(Hex._isHexValue(currentValue), 'Current value is not hexadecimal');

    return parseInt(currentValue, 16);
  }

  /**
   * Sets the current value from a decimal number
   *
   * @param {number} value - decimal value
   */
  setValueFromDecimal(value){
    assert(TypeCheck.isNumber(value), 'Value needs to be a number');

    this.setValue(value.toString(16));
  }

  /**
   * Implements input's validations
   *
   * @param {null|number} at - index used when input has been created as a vector that
   * tells which value should be used
   * @return {Promise<*>} value held by the input based on the current context (at)
   * @protected
   */
  _validation(at){

    // calling super class validations
    return super._validation(at).then((value) => {

      // hex value checking
      if (!Hex._isHexValue(value)){
        throw new ValidationFail(
          'Invalid hex value',
          '1747c406-2a14-4d0f-8a1d-1c554763e8a3',
        );
      }
      // value size checking
      else if (this.property('size') && (value.length / 2) * 8 > this.property('size')){
        throw new ValidationFail(
          'Value exceeds the maximum bit size',
          '0f8d2885-a4ac-4b7f-bb5b-32466c85363a',
        );
      }

      return value;
    });
  }

  /**
   * Auxiliary method that returns a boolean telling if the value is a valid
   * Hexadecimal value.
   *
   * @param {string} value - string containing the hex value
   * @return {boolean}
   * @private
   */
  static _isHexValue(value){
    return (TypeCheck.isString(value) && this._uuidFormatRegEx.test(value));
  }

  static _uuidFormatRegEx = /^[0-9a-fA-F]+$/;
}

// registering the input
Input.register(Hex);

// registering properties
Input.registerProperty(Hex, 'size');

module.exports = Hex;

const nodeIp = require('ip');
const ValidationFail = require('../Errors/ValidationFail');
const Input = require('../Input');
const BaseText = require('./BaseText');


/**
 * Ip address input.
 *
 * ```javascript
 * // ipv4
 * const input = Input.create('myInput: ip');
 * input.setValue('192.168.0.1');
 * ```
 *
 * ```javascript
 * // ipv6
 * const input = Input.create('myInput: ip');
 * input.setValue('::ffff:127.0.0.1');
 * ```
 *
 * ```javascript
 * // initializes the value of the input with 'remoteAddress'
 * // which is defined by the web handler
 * const input = Input.create('myInput: ip', {autofill: 'remoteAddress'});
 * console.log(input.value());
 * ```
 *
 * <h2>Property Summary</h2>
 *
 * Property Name | Description | Defined&nbsp;by Default | Default Value
 * --- | --- | :---: | :---:
 * allowV6 | boolean telling if the input allows ipv6 | ::on:: | ::true::
 *
 * All properties including the inherited ones can be listed via
 * {@link registeredPropertyNames}
 */
class Ip extends BaseText{

  /**
   * Returns a boolean telling if the value is ipv4
   *
   * @param {null|number} [at] - index used when input has been created as a vector that
   * tells which value should be used
   * @return {boolean}
   */
  isV4(at=null){

    const value = this.valueAt(at);
    return nodeIp.isV4Format(value);
  }

  /**
   * Returns a boolean telling if the value is ipv6
   *
   * @param {null|number} [at] - index used when input has been created as a vector that
   * tells which value should be used
   * @return {boolean}
