const assert = require('assert');
const TypeCheck = require('js-typecheck');
const Utils = require('./Utils');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _collection = Symbol('collection');


/**
 * Metadata provides a way for actions ({@link Action}) to control about how a
 * handler should perform the reading ({@link Reader}) and writing
 * ({@link Writer}) operations. Therefore, making possible handlers to perform
 * differently per action basis.
 *
 * This is done by defining {@link Reader} & {@link Writer} options through
 * the metadata. By doing that the options are passed from the action to the
 * handler during the handler's execution ({@link Handler.runAction}).
 *
 * You can define options by either using option variables or the full option location:
 *
 * **Option var (recommended):**
 * Eliminates the need of using convoluted long names to define the options by
 * simply using a variable that represents a full option location:
 *
 * Example:
 * ```
 * class MyAction extends Mebo.Action{
 *
 *    constructor(){
 *      super();
 *      // defining a custom uploadDirectory by using the `$webUploadDirectory` variable,
 *      // rather than the full option location (`handler.web.readOptions.uploadDirectory`)
 *      this.setMeta('$webUploadDirectory', '/tmp/customUploadDir');
 *    }
 *
 *    _perform(data){
 *      // ...
 *    }
 *
 *    _finalize(err, value){
 *      // defining a custom header that only affects the web handler
 *      // this call could be done inside of the _perform method. However, we
 *      // are defining it inside of the _finalize to keep _perform as
 *      // abstract as possible. Since, _finalize is always called (even during
 *      // an error) after the execution of the action, it provides a way to
 *      // hook and define custom metadata related with the result.
 *      if (!err){
 *        // defining a custom header by using the `$webHeaders` variable, rather
 *        // than the full option location (`handler.web.writeOptions.headers`)
 *        this.setMeta('$webHeaders', {
 *          someOption: 'foo',
 *        });
 *      }
 *
 *      return super._finalize(err, value);
 *    }
 * }
 * ```
 *
 * **Full option location:**
 * Uses a convention interpreted by the {@link Handler.metadata} to describe
 * where the option is localized:
 * ```
 * handler.<HANDLER_NAME>.<OPERATION>Options.<OPTION_NAME>
 * ```
 *
 * Example:
 * ```
 * class MyAction extends Mebo.Action{
 *    constructor(){
 *      super();
 *      this.setMeta('handler.web.readOptions.uploadDirectory', '/tmp/customUploadDir');
 *    }
 *
 *    _perform(data){
 *      // ...
 *    }
 *
 *    _finalize(err, value){
 *
 *      if (!err){
 *        // location (not recommended, see option var)
 *        this.setMeta('handler.web.writeOptions.headers', {
 *          someOption: 'foo',
 *        });
 *      }
 *
 *      return super._finalize(err, value);
 *    }
 * }
 * ```
 *
 * The complete list of the available option variables can be found bellow, it's
 * separated by handler type. Also, new variables can be assigned through
 * {@link Metadata.registerOptionVar}.
 *
 * <h2>Web Variables</h2>
 *
 * Variable name | Value | Value used by
 * --- | --- | ---
 * $web | `handler.web` |
 * $webUploadDirectory | `$web.readOptions.uploadDirectory` | {@link WebRequest}
 * $webUploadPreserveName | `$web.readOptions.uploadPreserveName` | {@link WebRequest}
 * $webUploadMaxFileSize | `$web.readOptions.uploadMaxFileSize` | {@link WebRequest}
 * $webMaxFields | `$web.readOptions.maxFields` | {@link WebRequest}
 * $webMaxFieldsSize | `$web.readOptions.maxFieldsSize` | {@link WebRequest}
 * $webHeaders | `$web.writeOptions.headers` | {@link WebResponse}
 * $webHeadersOnly | `$web.writeOptions.headersOnly`| {@link WebResponse}
 * $webResult | `$web.writeOptions.result`| {@link WebResponse}
 * $webRoot | `$web.writeOptions.root`| {@link WebResponse}
 * $webStatus | `$web.writeOptions.status`| {@link WebResponse}
 * $webResultLabel | `$web.writeOptions.resultLabel`| {@link WebResponse}
 *
 * <h2>Cli Variables</h2>
 *
 * Variable name | Value | Value used by
 * --- | --- | ---
 * $cli | `handler.cli` |
 * $cliDescription | `$cli.readOptions.description` | {@link CliArgs}
 * $cliResult | `$cli.writeOptions.result` | {@link CliOutput}
 */
class Metadata{

  /**
   * Creates a metadata
   */
  constructor(){
    this[_collection] = new Utils.HierarchicalCollection();
  }

  /**
   * Returns a value under the metadata.
   *
   * @param {string} path - path about where the value is localized (the levels
   * must be separated by '.'). In case of empty string the entire metadata
   * is returned.
   * @param {*} [defaultValue] - default value returned in case a value was
   * not found for the path
   * @return {*}
   */
  value(path, defaultValue=undefined){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');

    return this[_collection].query(Metadata._resolvePath(path), defaultValue);
  }

  /**
   * Sets a value to the metadata.
   *
   * @param {string} path - path about where the value should be stored under the metadata
   * (the levels must be separated by '.')
   * @param {*} value - value that is going to be stored under the collection
   * @param {boolean} [merge=true] - this option is used to decide in case of the
   * last level is already existing under the collection, if the value should be
   * either merged (default) or overridden.
   */
  setValue(path, value, merge=true){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');

    this[_collection].insert(Metadata._resolvePath(path), value, merge);
  }

  /**
   * Returns a list of the root levels under the metadata
   *
   * @return {Array<string>}
   */
  root(){
    return this[_collection].root();
  }

  /**
   * register an option variable under metadata
   *
   * The value of the variable can contain other pre-defined option variables,
   * for instance:
   * ```
   * Mebo.Metadata.registerOptionVar('$myVar', '$otherVar.data')
   * ```
   *
   * @param {string} name - name of the variable
   * @param {string} value - value for the variable
   */
  static registerOptionVar(name, value){
    assert(TypeCheck.isString(name), 'name needs to be defined as string');
    assert(TypeCheck.isString(value), 'value needs to be defined as string');
    assert((/^([\w_\$\.\-])+$/gi).test(value), `Illegal characters found variable (${name}) value: ${value}`); // eslint-disable-line no-useless-escape

    this._validateOptionVarName(name);

    // flushing cache
    this._cachedOptionVariables = {};

    // assigning variable
    this._optionVariables[name] = value;
  }

  /**
   * Returns the value for an option variable
   *
   * for instance:
   * ```
   * const myVariableValue = Mebo.Metadata.optionVar('$myVariable');
   * console.log(myVariableValue)
   * ```
   *
   * @param {string} name - name of the variable
   * @param {boolean} [processValue=true] - process any variables that may be
   * defined as part of the value
   * @return {string}
   *
   * @throws {Error} throws an error if the var name is undefined
   * under the metadata.
   */
  static optionVar(name, processValue=true){

    this._validateOptionVarName(name);
    if (!(name in this._optionVariables)){
      throw new Error(`Option variable ${name} is undefined`);
    }

    if (processValue){
      return this._resolveOptionVar(name);
    }

    return this._optionVariables[name];
  }

  /**
   * Returns a boolean telling if the variable name is defined
   *
   * @param {string} name - variable name
   * @return {boolean}
   */
  static hasOptionVar(name){
    this._validateOptionVarName(name);

    return (name in this._optionVariables);
  }

  /**
   * Returns a list of the registered variable names under the metadata
   *
   * @return {Array<string>}
   */
  static registeredOptionVars(){
    return Object.keys(this._optionVariables);
  }

  /**
   * Returns the path resolved by processing any variables that may
   * be defined as part of the path
   *
   * @param {string} path - path to be resolved
   * @return {string}
   */
  static _resolvePath(path){

    // if the path contains variables lets process it
    if (path.indexOf('$') !== -1){
      const processedPath = [];
      for (const part of path.split('.')){
        if (part.startsWith('$')){
          processedPath.push(Metadata.optionVar(part));
        }
        else{
          processedPath.push(part);
        }
      }

      // processed path
      return processedPath.join('.');
    }

    return path;
  }

  /**
   * Returns the value of the variable by processing any variables
   * that may be defined as part of the value
   *
   * @param {string} name - variable name
   * @param {string} [rootName] - root variable name used to report the origin
   * of the circular reference error
   * @param {number} [depth=0] - deep used to identify max recursion caused
   * by circular references
   * @return {string}
   * @private
   */
  static _resolveOptionVar(name, rootName, depth=0){

    // detecting circular references
    if (depth >= this._maxDepth){
      const error = new Error(`Circular reference detected while processing the value for $${rootName}`);

      // processing the stack of the error to get rid of the duplicated entries
      // caused by the recursion (we don't need to show 1000+ lines of the same
      // thing, the error explanation should be good enough)
      const stackContents = error.stack.split('\n');
      error.stack = stackContents.slice(0, 2).concat(stackContents.slice(this._maxDepth + 1)).join('\n');
      throw error;
    }

    // processing value
    const rawValue = this._optionVariables[name];

    // in case the value is not under the cache, lets process it
    if (!(name in this._cachedOptionVariables)){

      // checking if the value contains any variable
      let processedValue = rawValue;
      if (rawValue.indexOf('$') !== -1){

        // splitting the levels of the value that are separated by '.'
        const processedValueParts = [];
        for (const part of rawValue.split('.')){
          let processedPartValue;

          // in case of a variable
          if (part.startsWith('$')){

            this._validateOptionVarName(part);
            // processing variable value
            processedPartValue = this._resolveOptionVar(part, rootName || name, depth + 1);
          }
          // otherwise just use the part without any processing
          else{
            processedPartValue = part;
          }
          processedValueParts.push(processedPartValue);
        }

        // building back the structure of the value
        processedValue = processedValueParts.join('.');
      }

      // adding processed value to the cache
      this._cachedOptionVariables[name] = processedValue;
    }

    // returning value from cache
    return this._cachedOptionVariables[name];
  }

  /**
   * Check if the variable is defined using the proper syntax, otherwise in case
   * of any issues an exception is raised
   *
   * @param {string} name - variable name
   * @private
   */
  static _validateOptionVarName(name){
    assert(TypeCheck.isString(name), 'name needs to be a string');

    // check if variable name starts with $
    if (!name.startsWith('$')){
      throw new Error(`Option variable (${name}) needs to start with: $`);
    }

    // checking if variable is empty
    const cleanVarName = name.slice(1);
    if (!cleanVarName.length){
      throw new Error('Option variable cannot be empty');
    }

    // checking for invalid syntax
    else if (!(/^([\w_])+$/gi).test(cleanVarName)){
      throw new Error(`Option variable (${name}) contains invalid characters`);
    }
  }

  static _optionVariables = {};

  static _cachedOptionVariables = {};

  static _maxDepth = 1000;
}

module.exports = Metadata;

description:
Metadata provides a way for actions ({@link Action}) to control about how a
handler should perform the reading ({@link Reader}) and writing
({@link Writer}) operations. Therefore, making possible handlers to perform
differently per action basis.

This is done by defining {@link Reader} & {@link Writer} options through
the metadata. By doing that the options are passed from the action to the
handler during the handler's execution ({@link Handler.runAction}).

You can define options by either using option variables or the full option location:

**Option var (recommended):**
Eliminates the need of using convoluted long names to define the options by
simply using a variable that represents a full option location:

Example:
```
class MyAction extends Mebo.Action{

   constructor(){
     super();
     // defining a custom uploadDirectory by using the `$webUploadDirectory` variable,
     // rather than the full option location (`handler.web.readOptions.uploadDirectory`)
     this.setMeta('$webUploadDirectory', '/tmp/customUploadDir');
   }

   _perform(data){
     // ...
   }

   _finalize(err, value){
     // defining a custom header that only affects the web handler
     // this call could be done inside of the _perform method. However, we
     // are defining it inside of the _finalize to keep _perform as
     // abstract as possible. Since, _finalize is always called (even during
     // an error) after the execution of the action, it provides a way to
     // hook and define custom metadata related with the result.
     if (!err){
       // defining a custom header by using the `$webHeaders` variable, rather
       // than the full option location (`handler.web.writeOptions.headers`)
       this.setMeta('$webHeaders', {
         someOption: 'foo',
       });
     }

     return super._finalize(err, value);
   }
}
```

**Full option location:**
Uses a convention interpreted by the {@link Handler.metadata} to describe
where the option is localized:
```
handler.<HANDLER_NAME>.<OPERATION>Options.<OPTION_NAME>
```

Example:
```
class MyAction extends Mebo.Action{
   constructor(){
     super();
     this.setMeta('handler.web.readOptions.uploadDirectory', '/tmp/customUploadDir');
   }

   _perform(data){
     // ...
   }

   _finalize(err, value){

     if (!err){
       // location (not recommended, see option var)
       this.setMeta('handler.web.writeOptions.headers', {
         someOption: 'foo',
       });
     }

     return super._finalize(err, value);
   }
}
```

The complete list of the available option variables can be found bellow, it's
separated by handler type. Also, new variables can be assigned through
{@link Metadata.registerOptionVar}.

<h2>Web Variables</h2>

Variable name | Value | Value used by
--- | --- | ---
$web | `handler.web` |
$webUploadDirectory | `$web.readOptions.uploadDirectory` | {@link WebRequest}
$webUploadPreserveName | `$web.readOptions.uploadPreserveName` | {@link WebRequest}
$webUploadMaxFileSize | `$web.readOptions.uploadMaxFileSize` | {@link WebRequest}
$webMaxFields | `$web.readOptions.maxFields` | {@link WebRequest}
$webMaxFieldsSize | `$web.readOptions.maxFieldsSize` | {@link WebRequest}
$webHeaders | `$web.writeOptions.headers` | {@link WebResponse}
$webHeadersOnly | `$web.writeOptions.headersOnly`| {@link WebResponse}
$webResult | `$web.writeOptions.result`| {@link WebResponse}
$webRoot | `$web.writeOptions.root`| {@link WebResponse}
$webStatus | `$web.writeOptions.status`| {@link WebResponse}
$webResultLabel | `$web.writeOptions.resultLabel`| {@link WebResponse}

<h2>Cli Variables</h2>

Variable name | Value | Value used by
--- | --- | ---
$cli | `handler.cli` |
$cliDescription | `$cli.readOptions.description` | {@link CliArgs}
$cliResult | `$cli.writeOptions.result` | {@link CliOutput}

Returns the value for an option variable

for instance:
```
const myVariableValue = Mebo.Metadata.optionVar('$myVariable');
console.log(myVariableValue)
```

content:
const assert = require('assert');
const TypeCheck = require('js-typecheck');
const Action = require('./Action');
const Utils = require('./Utils');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _action = Symbol('action');
const _options = Symbol('options');
const _result = Symbol('result');


/**
 * A reader is used by the handler during the execution ({@link Handler.runAction})
 * to query the {@link Input} and {@link Session} information that is going be used
 * during the execution of the action.
 *
 * In case of new implements it's expected to implement the {@link Reader._perform}.
 *
 * When a value is found for an input it's decoded using {@link Input.parseValue}
 * where each input implementation has its own way of parsing the serialized data,
 * to find out about how a value is serialized for an specific input type you could simply
 * set an arbitrary value to an input then query it back through
 * {@link Input.serializeValue}. The reference bellow shows the basic serialization
 * for the inputs blundled with Mebo:
 *
 * Input Type | Scalar Serialization | Vector Serialization (compatible with JSON)
 * --- | --- | ---
 * {@link Text} | `'value'` | `'["valueA","valueB"]'`
 * {@link FilePath} | `'/tmp/a.txt'` | `'["/tmp/a.txt","/tmp/b.txt"]'`
 * {@link Bool} | `'true`' or `'1'` | `'[true,false]'` or `'[1,0]'`
 * {@link Numeric} | `'20'` | `'[20,30]'`
 * {@link Email} | `'test@email.com'` | `'["test@email.com","test2@email.com"]'`
 * {@link Ip} | `'192.168.0.1'` | `'["192.168.0.1","192.168.0.2"]'`
 * {@link Timestamp} | `'2017-02-02T22:26:30.431Z'` | \
 * `'["2017-02-02T22:26:30.431Z","2017-02-02T22:27:19.066Z"]'`
 * {@link Url} | `'#http#://www.google.com'` | \
 * `'["#http#://www.google.com","#http#://www.wikipedia.com"]'`
 * {@link Version} | `'10.1.1'` | `'["10.1.1","10.2"]'`
 * {@link Buf} | `'aGVsbG8='` | `'["aGVsbG8=","d29ybGQ="]'`
 * {@link Hex} | `'ffff00'` | `'["ffff00","ff"]'`
 * {@link Hash} | `'d65709ab'` | `'["d65709ab","b94d6fe4"]'`
 * {@link UUID} | `'075054e0-810a-11e6-8c1d-e5fb28c699ca'` | \
 * `'["075054e0-810a-11e6-8c1d-e5fb28c699ca","98e631d3-6255-402a-88bd-66056e1ca9df"]'`
 *
 * <br/>**Options:**
 * Custom options can be assigned to readers ({@link Reader.setOption}). They are
 * passed from the handler to the reader during the handler's execution
 * ({@link Handler.runAction}).
 *
 * ```
 * const myHandler = Mebo.Handler.create('someHandler');
 *
 * // setting reading options
 * myHandler.runAction('myAction', {
 *  someOption: 10,
 * });
 * ```
 *
 * When an action is executed through a handler it can define options via
 * the {@link Metadata} support. Detailed information about that can be found
 * at {@link Metadata}:
 *
 * ```
 * class MyAction extends Mebo.Action{
 *    constructor(){
 *      super();
 *
 *      // defining a custom reading option
 *      this.setMeta('$myOption', {
 *        someOption: 10,
 *      });
 *
 *      // ...
 *    }
 * }
 * Mebo.Action.register(MyAction, 'myAction');
 * ```
 *
 * **Hiding inputs from readers:**
 * A reader only sees inputs that are capable of serialization
 * ({@link Input.isSerializable}) or visible inputs. Therefore, any input assigned
 * with the property `hidden` is not visible by readers, for instance:
 *
 * ```
 * class Example extends Mebo.Action{
 *   constructor(){
 *     super();
 *     this.createInput('readerCantSeeMe: numeric', {hidden: true});
 *     this.createInput('readerSeeMe: numeric');
 *   }
 * }
 * ```
 */
class Reader{

  /**
   * Creates a reader.
   *
   * @param {Action} action - action used for the querying the values
   */
  constructor(action){

    // note: currently reader & writer are completely separated entities that don't
    // have a common parent class (aka HandlerOperation). The reason for
    // that is currently they are so distinctive from each other that the only member in
    // common is the option. In case they start to share more characteristics in common
    // then a base class should be created.

    this._setAction(action);
    this[_result] = null;
    this[_options] = new Utils.HierarchicalCollection();
  }

  /**
   * Returns the action associated with the reader.
   *
   * @return {Action}
   */
  action(){
    return this[_action];
  }

  /**
   * Returns an option
   *
   * @param {string} path - path about where the option is localized (the levels
   * must be separated by '.'). In case of an empty string it returns the
   * entire options
   * @param {*} [defaultValue] - default value returned in case a value was
   * not found for the path
   * @return {*}
   */
  option(path, defaultValue=undefined){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');
    return this[_options].query(path, defaultValue);
  }

  /**
   * Sets a value under the options
   *
   * @param {string} path - path about where the option should be stored under
   * the options (the levels must be separated by '.')
   * @param {*} value - value that is going to be stored under the collection
   * @param {boolean} [merge=true] - this option is used to decide in case of the
   * last level is already existing under the collection, if the value should be
   * either merged (default) or overridden.
   */
  setOption(path, value, merge=true){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');

    this[_options].insert(path, value, merge);
  }

  /**
   * Returns a list of valid input names that should be used for the parsing.
   * This avoids hidden inputs to get exposed in the parsing.
   *
   * @return {Array<string>}
   */
  validInputNames(){

    const inputs = [];
    for (const inputName of this[_action].inputNames()){
      const input = this[_action].input(inputName);

      if (input.isSerializable() && !input.property('hidden')){
        inputs.push(input);
      }
    }

    return inputs;
  }

  /**
   * Reads the input values and returns it through a plain object.
   *
   * @return {Promise<object>}
   */
  async inputValues(){

    if (!this[_result]){
      await this._parse();
    }

    return this[_result];
  }

  /**
   * Reads the autofill information and returns it through a plain object.
   *
   * If the autofill information is already assigned under autofill ({@link Action.session})
   * then that information is skipped otherwise it adds the parsed information the result.
   *
   * @return {Promise<Object>}
   */
  async autofillValues(){
    if (!this[_result]){
      await this._parse();
    }

    const result = Object.create(null);
    const action = this.action();
    const session = action.session();
    for (const inputName in this[_result]){

      const autofillName = action.input(inputName).property('autofill');

      if (autofillName){

        // if the input name is already under autofill (assigned previously
        // then not overriding them)
        if (session && session.hasAutofill(autofillName)){
          continue;
        }
        result[autofillName] = this[_result][inputName];
      }
    }

    return result;
  }

  /**
   * This method should be re-implemented by derived classes to perform the handler parsing.
   *
   * It should return a plain object containing the input name and the value for that.
   * Where any input value from either String or Array types are considered valid values that
   * are later ({@link Reader.inputValues}, {@link Reader.autofillValues})
   * used to parse the value of the input ({@link Input.parseValue}), otherwise the value
   * is ignored.
   *
   * Only return the ones that were found by the parsing. Also, in case of any error
   * during the parsing then an exception should be raised.
   *
   * @param {Array<Input>} inputList - Valid list of inputs that should be used for
   * the parsing
   * @return {Promise<Object>}
   *
   * @protected
   */
  _perform(inputList){
    return Promise.reject(new Error('Not implemented'));
  }

  /**
   * Sets the action associated with the reader.
   *
   * @param {Action} value - action instance
   */
  _setAction(value){
    assert(value instanceof Action, 'Invalid action');

    this[_action] = value;
  }

  /**
   * Auxiliary method that triggers the parsing if needed (in case it has not been
   * triggered yet).
   *
   * @return {Promise}
   * @private
   */
  async _parse(){
    if (!this._parsed){
      this[_result] = await this._perform(this.validInputNames());
      const action = this.action();

      // decoding the values if needed
      for (const inputName in this[_result]){
        const input = action.input(inputName);
        const value = this[_result][inputName];

        if (TypeCheck.isString(value)){
          this[_result][inputName] = input.parseValue(value, false);
        }
        else if (TypeCheck.isList(value)){
          // currently it's converting any array to a JSON string which is supported
          // by the input parsing. Lets keep an eye on this for now, since it may cause
          // an overhead
          this[_result][inputName] = input.parseValue(JSON.stringify(value), false);
        }
      }
    }

    return this[_result];
  }
}

module.exports = Reader;

description:
A reader is used by the handler during the execution ({@link Handler.runAction})
to query the {@link Input} and {@link Session} information that is going be used
during the execution of the action.

In case of new implements it's expected to implement the {@link Reader._perform}.

When a value is found for an input it's decoded using {@link Input.parseValue}
where each input implementation has its own way of parsing the serialized data,
to find out about how a value is serialized for an specific input type you could simply
set an arbitrary value to an input then query it back through
{@link Input.serializeValue}. The reference bellow shows the basic serialization
for the inputs blundled with Mebo:

Input Type | Scalar Serialization | Vector Serialization (compatible with JSON)
--- | --- | ---
{@link Text} | `'value'` | `'["valueA","valueB"]'`
{@link FilePath} | `'/tmp/a.txt'` | `'["/tmp/a.txt","/tmp/b.txt"]'`
{@link Bool} | `'true`' or `'1'` | `'[true,false]'` or `'[1,0]'`
{@link Numeric} | `'20'` | `'[20,30]'`
{@link Email} | `'test@email.com'` | `'["test@email.com","test2@email.com"]'`
{@link Ip} | `'192.168.0.1'` | `'["192.168.0.1","192.168.0.2"]'`
{@link Timestamp} | `'2017-02-02T22:26:30.431Z'` |  `'["2017-02-02T22:26:30.431Z","2017-02-02T22:27:19.066Z"]'`
{@link Url} | `'#http#://www.google.com'` |  `'["#http#://www.google.com","#http#://www.wikipedia.com"]'`
{@link Version} | `'10.1.1'` | `'["10.1.1","10.2"]'`
{@link Buf} | `'aGVsbG8='` | `'["aGVsbG8=","d29ybGQ="]'`
{@link Hex} | `'ffff00'` | `'["ffff00","ff"]'`
{@link Hash} | `'d65709ab'` | `'["d65709ab","b94d6fe4"]'`
{@link UUID} | `'075054e0-810a-11e6-8c1d-e5fb28c699ca'` |  `'["075054e0-810a-11e6-8c1d-e5fb28c699ca","98e631d3-6255-402a-88bd-66056e1ca9df"]'`

<br/>**Options:**
Custom options can be assigned to readers ({@link Reader.setOption}). They are
passed from the handler to the reader during the handler's execution
({@link Handler.runAction}).

```
const myHandler = Mebo.Handler.create('someHandler');

// setting reading options
myHandler.runAction('myAction', {
 someOption: 10,
});
```

When an action is executed through a handler it can define options via
the {@link Metadata} support. Detailed information about that can be found
at {@link Metadata}:

```
class MyAction extends Mebo.Action{
   constructor(){
     super();

     // defining a custom reading option
     this.setMeta('$myOption', {
       someOption: 10,
     });

     // ...
   }
}
Mebo.Action.register(MyAction, 'myAction');
```

**Hiding inputs from readers:**
A reader only sees inputs that are capable of serialization
({@link Input.isSerializable}) or visible inputs. Therefore, any input assigned
with the property `hidden` is not visible by readers, for instance:

```
class Example extends Mebo.Action{
  constructor(){
    super();
    this.createInput('readerCantSeeMe: numeric', {hidden: true});
    this.createInput('readerSeeMe: numeric');
  }
}
```

content:
const path = require('path');
const neodoc = require('neodoc');
const assert = require('assert');
const TypeCheck = require('js-typecheck');
const Inputs = require('../Inputs');
const Settings = require('../Settings');
const Handler = require('../Handler');
const Reader = require('../Reader');


// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _args = Symbol('args');

/**
 * command-line arguments reader.
 *
 * This reader is used by the {@link Cli} handler. It supports most of
 * the docopt specification. Also, if the reader finds an error it's capable of
 * reporting it in user-friendly way. This is used to report `-h/--help` and
 * missing arguments.
 *
 * All serializable inputs are supported by this handler, they can be displayed
 * either as `argument` or `option` element. This is done by setting the input
 * property `elementType` (option is the default one).
 *
 * You can define the description displayed in the help of the element by
 * setting the input property `description`.
 *
 * The `option` elements support `short option` by setting the input property
 * `shortOption`.
 *
 * In order to accommodate how vector values are represented in a command-line
 * interface, this reader expects vector elements to be separated by
 * the space character.
 *
 * Any {@link Bool} input specified as an `option` element behaves in a
 * special mode, since it's treated as a toogle option in command-line.
 * Therefore if the Bool input is assigned with a `true` then the option
 * gets the prefix `no-`.
 *
 * When a value is found for the input, it gets loaded via {@link Input.parseValue}
 * where each input implementation has its own way of parsing the serialized data,
 * to find out about how a value is serialized for an specific input type you could simply
 * set an arbitrary value to the input you are interested then query it back through
 * {@link Input.serializeValue}. Also, Mebo provides a reference datasheet
 * about the serialization forms for the inputs bundled with it, found at {@link Reader}.
 *
 * <h2>Options Summary</h2>
 *
 * Option Name | Description | Default Value
 * --- | --- | :---:
 * description | description about the program displayed at the header of the help \
 * | ::none::
 * parsingErrorStatusCode | Custom error status code used to identify when the \
 * app args could not be parsed | `700`
 *
 * @see http://docopt.org
 */
class CliArgs extends Reader{

  /**
   * Creates an args reader
   *
   * @param {Action} action - action that should be used by the reader
   * @param {Array<string>} argv - list of arguments that should be used by
   * the reader
   */
  constructor(action, argv){
    super(action);

    this._setArgs(argv);

    // default options
    this.setOption('description', '');
    this.setOption('parsingErrorStatusCode', 700);
  }

  /**
   * Returns a list of args used by the reader, by default it uses
   * `process.argv`.
   *
   * @return {Array<string>}
   */
  args(){
    return this[_args];
  }

  /**
   * Returns the executable name based on the args
   *
   * @return {string}
   */
  executableName(){
    return path.basename(this.args()[1]);
  }

  /**
   * Implements the reader
   *
   * @param {Array<Input>} inputList - Valid list of inputs that should be used for
   * the parsing
   * @return {Promise<Object>}
   * @protected
   */
  async _perform(inputList){
    const helpElements = await this.constructor._helpElements(inputList);
    const helpString = await this._helpInterface(helpElements);

    let parsedArgs = Object.create(null);
    // it thrown an exception if something went wrong (like missing a required parameter)
    try{
      parsedArgs = neodoc.run(helpString, {
        argv: (this.args().includes('--help') || this.args().includes('-h')) ? ['-h'] : this.args().slice(2),
        dontExit: true,
        smartOptions: true,
        repeatableOptions: true,
        version: Settings.get('apiVersion'),
      });
    }
    catch(err){
      // adding a custom status code for the parsing error
      // the error does not come as an instance of error, for this reason
      // creating a new error and copying the contents
      const error = Object.assign(new Error(), err);
      error.status = this.option('parsingErrorStatusCode');
      throw error;
    }

    // however when the user asks for the help it does not raises an exception
    if ('.help' in parsedArgs){
      const error = new Error(parsedArgs['.help']);
      error.status = this.option('parsingErrorStatusCode');
      throw error;
    }

    for (const input of inputList){
      if (input instanceof Inputs.Bool && !input.isVector()){
        input.setValue(Boolean(input.value()));
      }
    }

    const alreadyParsed = [];
    const result = Object.create(null);

    // collecting the input values
    for (const elementName in parsedArgs){
      let foundInputName;

      // finding the input name
      for (const elementType in helpElements){
        for (const inputName in helpElements[elementType]){
          const inputData = helpElements[elementType][inputName];
          if (inputData.usageDisplay.split('=')[0] === elementName || inputData.shortOption === elementName){
            foundInputName = inputName;
            break;
          }
        }

        if (foundInputName){
          break;
        }
      }

      // querying the input value
      const inputNames = inputList.map(x => x.name());
      if (foundInputName && !alreadyParsed.includes(foundInputName)){
        alreadyParsed.push(foundInputName);

        const input = inputList[inputNames.indexOf(foundInputName)];

        let value;
        if (TypeCheck.isBool(parsedArgs[elementName]) && !input.isVector()){
          value = String(!input.value());
        }
        else{
          if (input.isVector() && !TypeCheck.isList(parsedArgs[elementName])){
            value = [parsedArgs[elementName]];
          }
          else{
            value = parsedArgs[elementName];
          }
        }
        result[foundInputName] = value;
      }
    }

    return result;
  }

  /**
   * Sets a list of argument values used by the reader. It must follow
   * the same pattern found at `process.argv`
   *
   * @param {Array<string>} value - argument list
   * @private
   */
  _setArgs(value){
    assert(TypeCheck.isList(value), 'value needs to be a list');
    assert(value.length >= 2, 'missing first argument process.execPath and second argument javaScript file being executed');

    this[_args] = value.slice(0);
  }

  /**
   * Returns an object containing the elements that can be used by the command
   *
   * @param {Array<Input>} inputList - list of input that should be used to build
   * query the help
   * @return {Object}
   * @private
   */
  static async _helpElements(inputList){

    const elements = {
      argument: {},
      option: {},
    };

    // building inputs
    const addedArgs = [];
    const descriptions = await Promise.all(inputList.map(x => this._computeInfoDisplay(x)));

    let currentIndex = 0;
    for (const input of inputList){

      const inputName = input.name();
      let argName = this._camelCaseToArgument(inputName);

      // in case of a boolean input that is true by default adding
      // the `no` prefix to the input name automatically. For boolean inputs they
      // work as toggles when represented through the command line
      if (input instanceof Inputs.Bool && !input.isVector() && input.value()){
        argName = `no-${argName}`;
      }

      assert(!addedArgs.includes(argName), `Ambiguous argument name (${argName}), used multiple times!`);
      addedArgs.push(argName);

      const elementType = input.property('elementType');

      const inputData = Object.create(null);
      inputData.description = descriptions[currentIndex];
      inputData.elementDisplay = this._elementDisplay(argName, input);
      inputData.usageDisplay = this._usageDisplay(argName, input);
      inputData.required = ((input.isRequired() && input.isEmpty()) && !(input instanceof Inputs.Bool && !input.isVector()));
      inputData.vector = input.isVector();

      if (elementType === 'option'){
        inputData.shortOptionDisplay = this._shortOptionDisplay(input);
        inputData.shortOption = this._shortOption(input);
      }

      elements[elementType][inputName] = inputData;

      currentIndex++;
    }

    return elements;
  }

  /**
   * Returns the help interface displayed when `-h\--help` is required
   *
   * @param {Object} elements - elements holder object
   * @return {string}
   * @private
   */
  _helpInterface(elements){
    let output = '';
    output += this._buildDescription(elements);
    output += this._buildUsage(elements);
    output += this.constructor._buildColumns(elements);

    return output;
  }

  /**
   * Returns a string containing the full assembled info for the input
   *
   * @param {Input} input - input that should be used
   * @return {Promise<string>}
   * @private
   */
  static async _computeInfoDisplay(input){

    const inputTypeName = input.property('type');

    // adding the value type to the argument
    const isBoolInput = input instanceof Inputs.Bool;
    let description = input.property('description') || '';

    if ((isBoolInput && input.isVector()) || !isBoolInput){

      // adding the default value as part of the description
      if (!input.isEmpty()){
        let serializedValue = await input.serializeValue();
        serializedValue = (input.isVector()) ? JSON.parse(serializedValue) : [serializedValue];
        const defaultValue = [];

        for (const value of serializedValue){

          if (TypeCheck.isString(value) && Number.isNaN(Number(value))){
            const scapedValue = value.replace(new RegExp('"', 'g'), '\\"');
            defaultValue.push(`"${scapedValue}"`);
          }
          else{
            defaultValue.push(value);
          }
        }

        if (description.length){
          description += ' ';
        }
        description += `[default: ${defaultValue.join(' ')}]`;
      }
    }

    const inputTypeDisplay = input.isVector() ? `${inputTypeName}[]` : inputTypeName;

    if (description.length){
      description += ' ';
    }
    description += `(${inputTypeDisplay} type).`;

    return description;
  }

  /**
   * Returns a string containing the full element display for either an option
   * or argument
   *
   * @param {string} name - element given name
   * @param {Input} input - input that should be used
   * @return {string}
   * @private
   */
  static _elementDisplay(name, input){

    let result = '';

    if (input.property('elementType') === 'option'){
      const shortOption = this._shortOptionDisplay(input);

      const isBoolInput = input instanceof Inputs.Bool;
      if ((isBoolInput && input.isVector()) || !isBoolInput){

        // adding short option
        if (shortOption){
          result += shortOption;

          if (input.isVector()){
            result += '...';
          }

          result += ', ';
        }

        result += this._usageDisplay(name, input);

        if (input.isVector()){
          result += '...';
        }
      }
      else{
        if (shortOption){
          result += shortOption;
          result += ', ';
        }

        result += this._usageDisplay(name, input);
      }
    }
    else{
      result = name;
    }

    return result;
  }

  /**
   * Returns a string containing the usage display for either
   * the option or argument
   *
   * @param {string} name - how the element should be called
   * @param {Input} input - input that should be used
   * @return {string}
   * @private
   */
  static _usageDisplay(name, input){
    let result = '';

    if (input.property('elementType') === 'option'){
      // adding long option
      result = `--${name}`;

      const isBoolInput = input instanceof Inputs.Bool;
      if ((isBoolInput && input.isVector()) || !isBoolInput){
        result = `${result}=<value>`;
      }
    }
    else{
      result = `<${name}>`;
    }

    return result;
  }

  /**
   * Returns a string containing the the short option, in case the input
   * does not have a short option property defined then an empty string
   * is returned instead
   *
   * @param {Input} input - input that should be used
   * @return {string}
   * @private
   */
  static _shortOption(input){
    const shortOption = input.property('shortOption');
    if (shortOption){
      return `-${shortOption}`;
    }

    return '';
  }

  /**
   * Returns a string containing the display of the short option,
   * This is used when listing the element options
   *
   * @param {Input} input - input that should be used
   * @return {string}
   * @private
   */
  static _shortOptionDisplay(input){
    let result = this._shortOption(input);
    if (result.length && !(input instanceof Inputs.Bool && !input.isVector())){
      result = `${result}=<value>`;
    }

    return result;
  }

  /**
   * Returns a string containing the description of the command
   *
   * @param {Object} elements - elements holder object
   * @return {string}
   * @private
   */
  _buildDescription(elements){
    let output = '';
    const description = this.option('description');
    if (description.length){
      output += description;
      if (!description.endsWith('.')){
        output += '.';
      }
      output += '\n\n';
    }

    return output;
  }

  /**
   * Builds a string containing the usage
   *
   * @param {Object} elements - elements holder object
   * @return {string}
   * @private
   */
  _buildUsage(elements){
    let output = `Usage: ${this.executableName()} `;

    const requiredArguments = Object.create(null);
    const optionalArguments = Object.create(null);
    const requiredOptions = Object.keys(elements.option).filter(x => elements.option[x].required);
    let requiredArgumentsOrder = [];
    let optionalArgumentsOrder = [];

    if (requiredOptions.length){
      output += requiredOptions.map(x => elements.option[x].usageDisplay).join(' ');
      output += ' ';
    }
    output += '[options]';

    // building arguments
    if (Object.keys(elements.argument).length){
      for (const inputName in elements.argument){
        if (elements.argument[inputName].required){
          requiredArguments[inputName] = elements.argument[inputName];
        }
        else{
          optionalArguments[inputName] = elements.argument[inputName];
        }
      }

      const requiredArgumentNames = Object.keys(requiredArguments);
      requiredArgumentsOrder = requiredArgumentNames.filter(x => !requiredArguments[x].vector);
      requiredArgumentsOrder = requiredArgumentsOrder.concat(requiredArgumentNames.filter(x => !requiredArgumentsOrder.includes(x)));

      // first adding the required arguments
      let hasVectorRequiredArgument = false;
      for (const inputName of requiredArgumentsOrder){
        output += ' ';
        output += requiredArguments[inputName].usageDisplay;

        if (requiredArguments[inputName].vector && Object.keys(optionalArguments).length === 0){
          if (requiredArgumentsOrder.indexOf(inputName) === requiredArgumentsOrder.length - 1){
            output += '...';
            hasVectorRequiredArgument = true;
          }
        }
      }

      // then adding the optional ones
      const optionalArgumentNames = Object.keys(optionalArguments);
      optionalArgumentsOrder = optionalArgumentNames.filter(x => !optionalArguments[x].vector);
      optionalArgumentsOrder = optionalArgumentsOrder.concat(optionalArgumentNames.filter(x => !optionalArgumentsOrder.includes(x)));

      for (const inputName in optionalArguments){
        output += ' [';
        output += optionalArguments[inputName].usageDisplay;
        output += ']';

        if (optionalArguments[inputName].vector && !hasVectorRequiredArgument){
          if (optionalArgumentsOrder.indexOf(inputName) === optionalArgumentsOrder.length - 1){
            output += '...';
          }
        }
      }
    }

    output += this._buildUsageVectorOptions(elements, requiredArgumentsOrder, requiredOptions);

    return output;
  }

  /**
   * Builds a string containing the usage for the vector options
   *
   * @param {Object} elements - elements holder object
   * @param {Array<string>} argumentNames - list of argument names
   * @param {Array<string>} requiredOptionNames - list of required option names
   * @return {string}
   * @private
   */
  _buildUsageVectorOptions(elements, argumentNames, requiredOptionNames){
    let output = '';

    // adding the usage for the vector options
    for (const inputName in elements.option){

      if (elements.option[inputName].vector){
        output += `\n       ${this.executableName()} `;

        for (const requiredArg of argumentNames){
          output += elements.argument[requiredArg].usageDisplay;
          output += ' ';
        }

        output += '[options] ';
        if (requiredOptionNames.length){
          output += requiredOptionNames.filter(y => y !== inputName).map(x => elements.option[x].usageDisplay).join(' ');
          output += ' ';
        }

        if (!requiredOptionNames.includes(inputName)){
          output += '[';
        }

        output += elements.option[inputName].usageDisplay;
        output += '...';

        if (!requiredOptionNames.includes(inputName)){
          output += ']';
        }
      }
    }

    return output;
  }

  /**
   * Builds a string containing the columns displayed by the arguments and options
   *
   * @param {Object} elements - elements holder object
   * @return {string}
   * @private
   */
  static _buildColumns(elements){
    let columns = '\n';
    const elementTypeDisplayName = Object.create(null);
    elementTypeDisplayName.option = 'Options:';
    elementTypeDisplayName.argument = 'Arguments:';

    // figuring out the element column width
    const elementTypeWidth = this._computeElementsWidth(elements);

    for (const element in elements){

      if (Object.keys(elements[element]).length){
        columns += '\n';
        columns += elementTypeDisplayName[element];
        columns += '\n';

        for (const inputName in elements[element]){

          const elementData = elements[element][inputName];

          // element
          columns += '  ';
          columns += elementData.elementDisplay;
          columns += ' '.repeat(elementTypeWidth[element] - elementData.elementDisplay.length);

          // description
          // the second separator is actually a `hair space` char, this is necessary to separate
          // the element from the description in neodoc
          columns += '  ';
          columns += elementData.description;
          columns += '\n';
        }
      }
    }

    return columns;
  }

  /**
   * Returns a plain object containing the width for each of the element types
   * (argument and option)
   *
   * @param {Object} elements - elements holder object
   * @return {Object}
   * @private
   */
  static _computeElementsWidth(elements){
    const elementTypeWidth = Object.create(null);
    for (const elementType in elements){
      for (const inputName in elements[elementType]){
        elementTypeWidth[elementType] = Math.max(elementTypeWidth[elementType] || 0,
          elements[elementType][inputName].elementDisplay.length);
      }
    }

    return elementTypeWidth;
  }

  /**
   * Converts the input text from camelCase to dash-convention used
   * in CLI applications
   *
   * @param {string} text - text that should be converted
   * @return {string}
   * @private
   */
  static _camelCaseToArgument(text){
    return text.replace(/([a-z])([A-Z])/g, '$1-$2').toLowerCase();
  }
}

// registering reader
Handler.registerReader(CliArgs, 'cli');

module.exports = CliArgs;

const os = require('os');
const fs = require('fs');
const util = require('util');
const path = require('path');
const assert = require('assert');
const formidable = require('formidable');
const TypeCheck = require('js-typecheck');
const Settings = require('../Settings');
const Handler = require('../Handler');
const Reader = require('../Reader');
const Utils = require('../Utils');

// promisifying
const mkdtemp = util.promisify(fs.mkdtemp);
const rename = util.promisify(fs.rename);
const stat = util.promisify(fs.stat);
const rmdir = util.promisify(fs.rmdir);

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _temporaryFolders = Symbol('temporaryFolders');
const _request = Symbol('request');


/**
 * Web request reader.
 *
 * This reader is used by the {@link Web} handler to query values from a request.
 *
 * This reader supports all serializable inputs. It deals with file uploads
 * automatically; therefore any {@link FilePath} input becomes a potential
 * upload field. When that is the case the input gets assigned with the file path
 * about where the file has been uploaded to. By default it tries to keep the original
 * uploaded file name by replacing any illegal character with underscore, however you can
 * control this behavior via `uploadPreserveName` (if disabled each uploaded file
 * gets named with an unique name).
 *
 * This reader works by looking for the input names in the request, for instance:
 *
 * `http://.../?myInput=10&myOtherInput=20`
 *
 * ```
 * class MyAction extends Mebo.Action {
 *   constructor(){
 *     super();
 *     this.createInput('myInput: numeric');
 *     this.createInput('myOtherInput: numeric');
 *   }
 * }
 * ```
 *
 * When a value is found for the input, it gets loaded via {@link Input.parseValue}
 * where each input implementation has its own way of parsing the serialized data,
 * to find out about how a value is serialized for an specific input type you could simply
 * set an arbitrary value to the input you are interested then query it back through
 * {@link Input.serializeValue}. Also, Mebo provides a reference datasheet
 * about the serialization forms for the inputs bundled with it, found at {@link Reader}.
 *
 * **Route parameters:**
 * If an webfied action contains route parameters defined (`/users/:userId/books/:bookId`)
 * this reader is going to try to find them under the action input names.
 * Therefore when a route parameter matches to the name of an input then the value of
 * the parameter is loaded to the input.
 *
 * **Vector Inputs:**
 * Supported conventions for array parameters:
 *
 * - *Serialized vector value (JSON Style)*
 * ```
 * http://.../?vectorInput=["a", "b", "c"]
 * ```
 *
 * - *Repeated param names*
 * ```
 * http://.../?vectorInput[]=a&vectorInput[]=b&vectorInput[]=c
 * ```
 * *or*
 * ```
 * http://.../?vectorInput=a&vectorInput=b&vectorInput=c
 * ```
 *
 * <h2>Options Summary</h2>
 *
 * Option Name | Description
 * --- | ---
 * uploadDirectory | directory used for placing file uploads in, default value\
 * (`TMP_DIR/upload`) driven by:\
 * <br>`Settings.get('reader/webRequest/uploadDirectory')`
 * uploadPreserveName | enabled by default it tries to keep the original final name of \
 * uploads, any illegal character is replaced by underscore, otherwise if disabled \
 * it gives a random name to the upload, default value driven by: \
 * <br>`Settings.get('reader/webRequest/uploadPreserveName')`
 * uploadMaxFileSize | total maximum file size about all uploads in bytes, \
 * default value (`4 mb`) driven by: \
 * <br>`Settings.get('reader/webRequest/uploadMaxFileSize')`
 * maxFields | Limits the number of fields that the querystring parser will decode, \
 * default value (`1000`) driven by: \
 * <br>`Settings.get('reader/webRequest/maxFields')`
 * maxFieldsSize | Limits the amount of memory all fields together (except files) can\
 * allocate in bytes, default value (`2 mb`) driven by:\
 * <br>`Settings.get('reader/webRequest/maxFieldsSize')` [`2 mb`]
 *
 * <br/>Example about defining a custom `uploadMaxFileSize` option from inside of an
 * action through the metadata support:
 *
 * ```
 * class MyAction extends Mebo.Action{
 *    constructor(){
 *      super();
 *
 *      // 'uploadMaxFileSize' option
 *      this.setMeta('handler.web.readOptions', {
 *        uploadMaxFileSize: 10 * 1024 * 1024,
 *      });
 *    }
 * }
 * ```
 */
class WebRequest extends Reader{

  /**
   * Creates a web request reader
   *
   * @param {Action} action - action that should be used by the reader
   * @param {Object} req - request object created by express-js
   */
  constructor(action, req){
    super(action);

    this._setRequest(req);

    // default options
    this.setOption('uploadDirectory', Settings.get('reader/webRequest/uploadDirectory'));
    this.setOption('uploadPreserveName', Settings.get('reader/webRequest/uploadPreserveName'));
    this.setOption('uploadMaxFileSize', Settings.get('reader/webRequest/uploadMaxFileSize'));
    this.setOption('maxFields', Settings.get('reader/webRequest/maxFields'));
    this.setOption('maxFieldsSize', Settings.get('reader/webRequest/maxFieldsSize'));

    this[_temporaryFolders] = [];
  }

  /**
   * Returns the request object created by express
   *
   * @return {Object}
   * @see http://expressjs.com/en/api.html#req
   */
  request(){
    return this[_request];
  }

  /**
   * Implements the web request reader
   *
   * @param {Array<Input>} inputList - Valid list of inputs that should be used for
   * the parsing
   * @return {Promise<Object>}
   * @protected
   */
  async _perform(inputList){
    const result = Object.create(null);
    const request = this.request();

    // handling body fields
    let bodyFields = null;
    if (['POST', 'PUT', 'PATCH'].includes(request.method)){
      bodyFields = await this._bodyFields(inputList);
    }

    // setting the action inputs based on the request parameters
    for (const input of inputList){
      const inputName = input.name();

      // value set by the request
      let requestInputValue;
      const restrictWebAccess = input.hasProperty('restrictWebAccess') ? input.property('restrictWebAccess') : false;

      // mapping param to input names
      if (!restrictWebAccess && inputName in request.params){
        requestInputValue = request.params[inputName];
      }
      // body fields
      else if (bodyFields !== null){
        if (restrictWebAccess && inputName in bodyFields.files){
          requestInputValue = bodyFields.files[inputName];
        }
        else if (!restrictWebAccess){

          if (inputName in bodyFields.files){
            requestInputValue = bodyFields.files[inputName];
          }
          else if (inputName in bodyFields.fields){
            requestInputValue = bodyFields.fields[inputName];
          }
        }
      }

      // GET, DELETE ...
      else if (!restrictWebAccess && inputName in request.query){
        requestInputValue = request.query[inputName];
      }

      if (requestInputValue !== undefined){
        result[inputName] = requestInputValue;
      }
    }

    return result;
  }

  /**
   * Sets the request object created by express
   *
   * @param {Object} value - req object
   * @see http://expressjs.com/en/api.html#req
   * @private
   */
  _setRequest(value){
    assert(TypeCheck.isObject(value) && value.method, 'Invalid request object');

    this[_request] = value;
  }

  /**
   * Returns an object containing the processed body fields parsed, this object separates
   * the fields from the files
   *
   * @return {Promise<Object>}
   * @private
   */
  async _bodyFields(){

    // making sure the upload directory exists
    const uploadDirectory = this.option('uploadDirectory');
    if (uploadDirectory && !WebRequest._checkedUploadDirectories.includes(uploadDirectory)){

      // in case the stat fails it will try to recreate the folders
      let needsToCreate = false;
      try{
        await stat(uploadDirectory);
      }
      // otherwise tries to create it
      catch(err){

        // file not found
        if (err.code === 'ENOENT'){
          needsToCreate = true;
        }
        else{
          /* istanbul ignore next */
          throw err;
        }
      }

      if (needsToCreate){
        await Utils.mkdirs(uploadDirectory);
      }

      WebRequest._checkedUploadDirectories.push(uploadDirectory);
    }

    // parsing the body fields
    const bodyFields = await this._parseForm();

    // normalizing multiple values for the fields
    this._normalizeFieldMultipleValues(bodyFields);

    // handling the uploaded files
    await this._handleUploadedFiles(bodyFields);

    return bodyFields;
  }

  /**
   * Normalizing multiple values for the fields by adding the values to an array
   * followed by the name of the field (field=[value1, value2...]) rather than
   * having an individual field entry for each of the indexes of the array
   * (field[0]=value1, field[1]=value2...)
   *
   * @param {Object} bodyFields - parsed body object
   * @private
   */
  _normalizeFieldMultipleValues(bodyFields){

    const multipleValueFields = Object.create(null);

    for (const inputName in bodyFields.fields){

      // checking if there is any array field if so extracting their name and value
      if (inputName.endsWith(']')){
        const inputParts = inputName.split('[');
        if (inputParts.length === 2){
          if (!(inputParts[0] in multipleValueFields)){
            multipleValueFields[inputParts[0]] = [];
          }

          multipleValueFields[inputParts[0]].push(bodyFields.fields[inputName]);
        }
      }
    }

    // merging the normalized multiple values to the original fields
    Object.assign(bodyFields.fields, multipleValueFields);
  }

  /**
   * Handles the uploaded files (changes bodyFields in-place)
   *
   * @param {Object} bodyFields - parsed body object
   * @private
   */
  async _handleUploadedFiles(bodyFields){
    const keepOrignalNamePromises = new Map();

    const preserveFileName = this.option('uploadPreserveName');
    for (const inputName in bodyFields.files){
      // multiple files
      if (TypeCheck.isList(bodyFields.files[inputName])){

        for (const index in bodyFields.files[inputName]){
          if (preserveFileName){
            keepOrignalNamePromises.set([inputName, index], this._keepOriginalUploadName(bodyFields.files[inputName][index]));
          }
          else{
            bodyFields.files[inputName][index] = bodyFields.files[inputName][index].path;
          }
        }
      }

      // single file
      else{
        if (preserveFileName){
          keepOrignalNamePromises.set([inputName], this._keepOriginalUploadName(bodyFields.files[inputName]));
        }
        else{
          bodyFields.files[inputName] = bodyFields.files[inputName].path;
        }
      }
    }

    // 'keep original name' is done in parallel for all files at once
    if (keepOrignalNamePromises.size){
      const originalNameKeys = Array.from(keepOrignalNamePromises.keys());
      const originalNameValues = await Promise.all(keepOrignalNamePromises.values());

      for (let i=0, len=keepOrignalNamePromises.size; i < len; ++i){
        // single
        if (originalNameKeys[i].length === 1){
          bodyFields.files[originalNameKeys[i][0]] = originalNameValues[i];
        }
        // multi
        else{
          bodyFields.files[originalNameKeys[i][0]][originalNameKeys[i][1]] = originalNameValues[i];
        }
      }
    }

    // adding the cleanup temporary folders to the wrapup tasks
    if (this[_temporaryFolders].length){
      this.action().session().wrapup().addWrappedPromise(
        this._cleanupTemporaryFolders.bind(this),
        {
          priority: 1000,
        },
      );
    }
  }

  /**
   * Auxiliary method used to promisify formidable's form.parse call
   *
   * @return {Promise<Object>}
   * @private
   */
  _parseForm(){
    return new Promise((resolve, reject) => {

      const form = new formidable.IncomingForm();

      // formidable settings
      form.uploadDir = this.option('uploadDirectory');
      form.maxFileSize = this.option('uploadMaxFileSize');
      form.keepExtensions = true;
      form.multiples = true;
      form.encoding = 'utf-8';
      form.maxFields = this.option('maxFields');
      form.maxFieldsSize = this.option('maxFieldsSize');

      form.parse(this.request(), (err, formFields, formFiles) => {

        // in case of any error
        /* istanbul ignore next */
        if (err){
          err.status = err.status || 500;
          reject(err);
          return;
        }

        const result = Object.create(null);
        result.files = formFiles;
        result.fields = formFields;

        resolve(result);
      });
    });
  }

  /**
   * Renames the uploaded file names which receive random unique names to the original uploaded file name,
   * this is done by creating an intermediated unique name folder for each of the upload files then
   * renaming them back to the original name.
   * This method is called when `uploadPreserveName` returns true
   *
   * @param {string} uploadFile - input file name
   * @return {string} output file name
   * @private
   */
  async _keepOriginalUploadName(uploadFile){
    const uploadFolder = await mkdtemp(path.join(this.option('uploadDirectory'), 'file-'));
    const finalName = path.join(uploadFolder, uploadFile.name.replace(/[^a-zA-Z0-9 _.-]/g, '_'));
    await rename(uploadFile.path, finalName);

    // temporary folders removed at the end of the request
    this[_temporaryFolders].push(uploadFolder);

    return finalName;
  }

  /**
   * Promise based method that removes the temporary folders that are created
   * when `uploadPreserveName` is enabled
   *
   * @return {Promise}
   * @private
   */
  _cleanupTemporaryFolders(){
    const result = Promise.all(
      this[_temporaryFolders].map(x => this._deleteTemporaryFolder(x)),
    );

    // theoretically this method can be called multiple times by handler.run
    // for the same request
    this[_temporaryFolders] = [];

    return result;
  }

  /**
   * Deletes a temporary folder
   *
   * @param {string} folder - folder to be deleted
   * @return {Promise}
   * @private
   */
  _deleteTemporaryFolder(folder){
    return rmdir(folder).then((result) => {
      // runaway promise
      return null;
    }).catch(/* istanbul ignore next */ (err) => {
      if (!(['ENOTEMPTY', 'ENOENT'].includes(err.code))){
        throw err;
      }
    });
  }

  static _checkedUploadDirectories = [];
}

// default settings
Settings.set('reader/webRequest/uploadDirectory', path.join(os.tmpdir(), 'upload'));
Settings.set('reader/webRequest/uploadMaxFileSize', 4 * 1024 * 1024);
Settings.set('reader/webRequest/uploadPreserveName', true);
Settings.set('reader/webRequest/maxFields', 1000);
Settings.set('reader/webRequest/maxFieldsSize', 2 * 1024 * 1024);

// registering reader
Handler.registerReader(WebRequest, 'web');

module.exports = WebRequest;

const assert = require('assert');
const TypeCheck = require('js-typecheck');
const Tasks = require('./Tasks');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _arbitraryData = Symbol('arbitraryData');
const _autofill = Symbol('autofill');
const _wrapup = Symbol('wrapup');
const _terminated = Symbol('terminated');


/**
 * A session is used to store the data shared between actions.
 *
 * This object is created automatically by {@link Handler.create} and
 * {@link Action.create}.
 */
class Session{

  /**
   * Creates a session
   *
   * @param {Tasks} [wrapup] - task object used to hold actions and promises that are triggered
   * when finalizing ({@link finalize}) the session
   */
  constructor(wrapup=null){

    assert(wrapup === null || wrapup instanceof Tasks, 'wrapup needs to defined with a Tasks object or null');

    this[_wrapup] = wrapup || new Tasks();
    this[_terminated] = false;

    // container used to store autofill values inside of the session
    this[_autofill] = {};

    // generic container used to store arbitrary inside of the session
    this[_arbitraryData] = {};
  }

  /**
   * Returns the tasks object used to hold actions and promises that are triggered
   * when finalizing ({@link finalize}) the session. Wrapup actions can be used to avoid
   * the execution of an action that may be triggered multiple times across
   * nested actions where ideally it should be executed only once in the end,
   * after all nested actions are done.
   *
   * @return {Tasks}
   */
  wrapup(){
    return this[_wrapup];
  }

  /**
   * Returns a value under the autofill data.
   *
   * This feature is used to set the initial input value for inputs that contain the `autofill`
   * property. It works by looking if the value of the autofill input's property is under the
   * {@link Session.autofill} then if found it sets the input value with the value of the
   * {@link Session.autofill}. This process occurs when a session is assigned to the action
   * ({@link Action.setSession}).
   *
   * When inputs that contain the property `autofill` are initialized through the
   * {@link Handler} they will try to find their values under the autofill,
   * however if a value is not defined for them they will assign their input value
   * to the autofill data automatically.
   *
   * @param {string} key - key name used to query the autofill value
   * @param {*} [defaultValue] - optional default value returned in case
   * the key does not exist
   * @return {*}
   */
  autofill(key, defaultValue=undefined){
    assert(TypeCheck.isString(key), 'key needs to defined as string');

    if (key in this[_autofill]){
      return this[_autofill][key];
    }

    return defaultValue;
  }

  /**
   * Returns a boolean telling if the key exists under the autofill data.
   *
   * @param {string} key - key name
   * @return {boolean}
   */
  hasAutofill(key){
    return (key in this[_autofill]);
  }

  /**
   * Sets a key & value under the autofill data
   *
   * @param {string} key - key name
   * @param {*} value - value associated with the key
   *
   * @see {@link Session.autofill}
   */
  setAutofill(key, value){
    assert(TypeCheck.isString(key), 'key needs to defined as string');

    this[_autofill][key] = value;
  }

  /**
   * Returns the keys included in the autofill data
   *
   * @return {Array<string>}
   */
  autofillKeys(){
    return Object.keys(this[_autofill]);
  }

  /**
   * Sets a key & value under the arbitrary data. This is used to store data
   * that may be only available in specific handlers, for instance the
   * request object created by express is assigned to the session through this
   * method by the web handler.
   *
   * @param {string} key - name of the key
   * @param {*} value - value for the key
   */
  set(key, value){
    assert(TypeCheck.isString(key), 'key needs to defined as string');

    this[_arbitraryData][key] = value;
  }

  /**
   * Returns an value assigned for the key inside of the arbitrary data
   *
   * @param {string} key - name of the key
   * @param {*} [defaultValue] - optional value returned when the key is not assigned
   * @return {*}
   */
  get(key, defaultValue=undefined){
    if (key in this[_arbitraryData]){
      return this[_arbitraryData][key];
    }

    return defaultValue;
  }

  /**
   * Returns a boolean telling if the input key is under the arbitrary data
   *
   * @param {string} key - key name
   * @return {boolean}
   */
  has(key){
    return (key in this[_arbitraryData]);
  }

  /**
   * Returns the keys included in the arbitrary data
   *
   * @return {Array<string>}
   */
  keys(){
    return Object.keys(this[_arbitraryData]);
  }

  /**
   * Terminates the session by executing the {@link wrapup}
   * tasks.
   *
   * This is called by the {@link Handler} during the execution of
   * {@link Handler.output}.
   *
   * @return {Promise}
   */
  async finalize(){

    const wrapup = this.wrapup();
    if (!wrapup.isEmpty()){

      await wrapup.run();
      wrapup.clear();
    }

    return true;
  }

  /**
   * Returns a cloned version of the current session where all autofill
   * and arbitrary data are transferred to the cloned version.
   *
   * This feature is used by the actions during the session assignment
   * ({@link Action.setSession}) to prevent that modifications done in the session
   * of nested actions reflect back in the session used by the parent actions.
   * Therefore, acting as scope for changes in the autofill & arbitrary data.
   *
   * The current wrapup is also assigned to the cloned version.
   *
   * @return {Session}
   */
  clone(){
    const result = new Session(this.wrapup());

    // transferring autofill data
    for (const autofillKey in this[_autofill]){
      result.setAutofill(autofillKey, this[_autofill][autofillKey]);
    }

    // transferring arbitrary data
    for (const arbitraryKey in this[_arbitraryData]){
      result.set(arbitraryKey, this[_arbitraryData][arbitraryKey]);
    }

    return result;
  }
}

module.exports = Session;

