const assert = require('assert');
const TypeCheck = require('js-typecheck');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _contents = Symbol('contents');
const _executedCount = Symbol('executedIndex');


/**
 * Object that holds actions and promises, which are executed at specific events.
 *
 * Inside Mebo tasks are used by the {@link Session.wrapup} to hold actions and
 * promises that are triggered when a {@link Session} is about to be terminated
 * ({@link Session.finalize}).
 */
class Tasks{

  /**
   * Creates a new instance
   */
  constructor(){
    this[_contents] = [];
    this[_executedCount] = 0;
  }

  /**
   * Adds an action to the tasks
   *
   * `runOnlyOnce` can be used to avoid the execution of an action that may be triggered
   * multiple times across nested actions where ideally it should be executed only once,
   * it's done by using the action's id ({@link Action.id}).
   *
   * @param {Action} action - action instance that should be executed in the wrap up
   * @param {Object} options - custom options
   * @param {boolean} [options.runOnlyOnce=true] - tells if the action should be ignore in case
   * it has already been executed previously (it's done by matching the {@link Action.id})
   * @param {number} [options.priority=100] - tells the priority of the action, this affects
   * the execution order where a lower numeric value means a higher priority.
   */
  addAction(action, {runOnlyOnce=true, priority=100}={}){
    assert(TypeCheck.isCallable(action.run), 'Invalid Action');

    this[_contents].push({
      type: 'action',
      item: action,
      onlyOnce: runOnlyOnce,
      execPriority: priority,
    });
  }

  /**
   * Adds a wrapped promise to the tasks
   *
   * @param {function} wrappedPromise - function that should return a promise, ex:
   * ```javascript
   * tasks.addWrappedPromise(() => Promise.resolve(3))
   * ```
   * @param {Object} options - custom options
   * @param {number} [options.priority=100] - tells the priority of the action, this affects
   * the execution order where a lower numeric value means a higher priority.
   */
  addWrappedPromise(wrappedPromise, {priority=100}={}){
    assert(TypeCheck.isCallable(wrappedPromise), 'Promise needs to wrapped into a function');

    this[_contents].push({
      type: 'promise',
      item: wrappedPromise,
      execPriority: priority,
    });
  }

  /**
   * Returns a list sorted by priority and inclusion order about the actions and
   * promises that are executed through {@link execute}
   *
   * @param {boolean} [actions=true] - tells if the result should return the actions
   * @param {boolean} [promises=true] - tells if the result should return the promises
   * @return {Promise<Array>}
   */
  async contents(actions=true, promises=true){
    const result = [];

    const taskOrder = this[_contents].map((data, index) => {
      return {
        i: index,
        contents: data,
      };
    });

    taskOrder.sort((a, b) => {
      if (a.contents.execPriority < b.contents.execPriority) return -1;
      if (a.contents.execPriority > b.contents.execPriority) return 1;
      return a.i - b.i;
    });

    const finalTaskOrder = taskOrder.map(x => x.contents);

    let resultIndex = 0;
    const actionIdMap = new Map();
    for (const task of finalTaskOrder){
      // actions
      if (actions && task.type === 'action'){
        actionIdMap.set(resultIndex, task.item.id());
        result.push(task.item);
      }

      // promises
      else if (promises && task.type === 'promise'){
        result.push(task.item);
      }

      resultIndex++;
    }

    // the process bellow executes the ids in parallel and removes the duplicated
    // action id for any action marked as run onlyOnce from the final result
    const actionIndexes = Array.from(actionIdMap.keys());
    const actionIdResults = await Promise.all(actionIdMap.values());
    const alreadyAddedIds = [];

    let keyIndex = 0;
    let removedCount = 0;
    for (const actionIndex of actionIndexes){

      const actionId = actionIdResults[keyIndex];
      const alreadyIncluded = alreadyAddedIds.includes(actionId);

      if (alreadyIncluded && this[_contents][actionIndex].onlyOnce){
        result.splice(actionIndex - removedCount, 1);

        removedCount++;
      }
      else if (!alreadyIncluded){
        alreadyAddedIds.push(actionId);
      }

      keyIndex++;
    }

    return result;
  }

  /**
   * Tells if there are no tasks
   *
   * @return {boolean}
   */
  isEmpty(){
    return this[_contents].length === 0;
  }

  /**
   * Resets the tasks by cleaning all actions and promises
   *
   */
  clear(){
    this[_contents].length = 0;
    this[_executedCount] = 0;
  }

  /**
   * Executes the actions and promises inside of the tasks
   * (provided by {@link contents}). All tasks get executed even if an error occurs
   * during the execution of an specific task. In case this method is triggered
   * multiple times it ensures each task is only executed once.
   *
   * Failed tasks are reported through an exception raised after all tasks have been executed,
   * this error provides the `taskErrors` member that contains a list about the errors raised
   * during the execution of the tasks.
   *
   * @return {Promise<Array>} Returns an array containing each result of the tasks
   */
  async run(){

    const contents = await this.contents();
    const taskErrors = [];
    const result = [];

    let currentIndex = 0;
    for (const task of contents){

      // skipping tasks that have been executed previously
      currentIndex++;
      if (this[_executedCount] >= currentIndex){
        continue;
      }
      else{
        this[_executedCount]++;
      }

      // executing task
      try{
        result.push(
          await ((TypeCheck.isCallable(task)) ? task() : task.run()), // eslint-disable-line no-await-in-loop
        );
      }
      catch(err){
        taskErrors.push(err);
      }

    }

    // throwing a new exception about the failed tasks
    if (taskErrors.length){
      const error = new Error(`Failed to execute ${taskErrors.length} tasks (out of ${contents.length})`);

      // including the stack trace information from the failed tasks (debugging purposes)
      for (const taskError of taskErrors){
        error.stack += `\n\nTask stack trace:\n${taskError.stack}`;
      }

      // also, including the task errors to the error itself (debugging purposes)
      error.taskErrors = taskErrors;

      throw error;
    }

    return result;
  }
}

module.exports = Tasks;

const assert = require('assert');
const TypeCheck = require('js-typecheck');
const deepMerge = require('./deepMerge');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _collection = Symbol('collection');


/**
 * Provides an interface to deal with data in a multi dimension plain object.
 *
 * It automatically creates the intermediate levels when assigning a value under
 * nested levels to the collection. Also, if the value already exists
 * under the collection then it allows the value to be merged with the
 * existing values.
 * @private
 */
class HierarchicalCollection{

  /**
   * Creates a new instance
   */
  constructor(){
    this[_collection] = {};
  }

  /**
   * Inserts a new value to the collection
   *
   * @param {string} path - path about where the value should be stored (the levels
   * must be separated by '.').
   * @param {*} value - value that is going to be stored under the collection
   * @param {boolean} [merge=true] - this option is used to decide in case of the
   * last level is already existing under the collection, if the value should be
   * either merged (default) or overridden.
   */
  insert(path, value, merge=true){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');
    assert(path.length, 'path cannot be empty');
    assert((/^([\w_\.\-])+$/gi).test(path), `Illegal path name: ${path}`); // eslint-disable-line no-useless-escape

    let currentLevel = this[_collection];
    let finalLevel = path;

    // building the intermediate levels if necessary
    if (path.indexOf('.') !== -1){
      const levels = path.split('.');
      for (const level of levels.slice(0, -1)){
        if (!(level in currentLevel)){
          currentLevel[level] = {};
        }

        currentLevel = currentLevel[level];
      }
      finalLevel = levels.slice(-1)[0];
    }

    // assigning value
    if (merge && TypeCheck.isPlainObject(value) && finalLevel in currentLevel){
      const merged = deepMerge(currentLevel[finalLevel], value);
      Object.assign(currentLevel[finalLevel], merged);
    }
    else{
      currentLevel[finalLevel] = value;
    }
  }

  /**
   * Returns a value under the collection
   *
   * @param {string} path - path about where the value is localized (the levels
   * must be separated by '.'). In case of empty string the entire collection
   * is returned.
   * @param {*} [defaultValue] - default value returned in case a value was
   * not found for the path
   * @return {*}
   */
  query(path, defaultValue=undefined){
    assert(TypeCheck.isString(path), 'path needs to be defined as string');

    let currentLevel = this[_collection];

    // returning the entire collection
    if (!path.length){
      return currentLevel;
    }

    // no intermediate levels
    if (path.indexOf('.') === -1){
      if (!(path in currentLevel)){
        return defaultValue;
      }
      return currentLevel[path];
    }

    // otherwise find the value going through the intermediate levels
    const levels = path.split('.');
    for (const level of levels){
      if (!(level in currentLevel)){
        return defaultValue;
      }

      currentLevel = currentLevel[level];
    }

    return currentLevel;
  }

  /**
   * Returns a list of the root levels
   *
   * @return {Array<string>}
   */
  root(){
    return Object.keys(this[_collection]);
  }
}

module.exports = HierarchicalCollection;

const TypeCheck = require('js-typecheck');


/**
 * Map implementation designed to hold immutable data.
 *
 * This object makes sure that values held by the map cannot have their data
 * modified.
 */
class ImmutableMap extends Map{

  /**
   * Sets a key and value to the map
   *
   * @param {*} key - key associated with the value
   * @param {*} value - value that will be stored immutable
   */
  set(key, value){
    super.set(key, this._deepFreeze(value));
  }

  /**
   * Auxiliary method that recursively makes the input object immutable
   *
   * @param {object} value - object that should become immutable
   * @return {object}
   * @private
   */
  _deepFreeze(value){

    if (TypeCheck.isNone(value) || value instanceof Buffer){
      return value;
    }

    // freezing all properties
    for (const propertyName of Object.getOwnPropertyNames(value)){
      const property = value[propertyName];

      // freeze prop if it is an object
      if (TypeCheck.isObject(property)){
        this._deepFreeze(property);
      }
    }

    return Object.freeze(value);
  }
}

module.exports = ImmutableMap;

const assert = require('assert');
const TypeCheck = require('js-typecheck');
const LruCacheBase = require('lru-cache');
const sizeof = require('object-sizeof');

// symbols used for private instance variables to avoid any potential clashing
// caused by re-implementations
const _size = Symbol('size');
const _lifespan = Symbol('lifespan');
const _cache = Symbol('cache');


/**
 * A cache that discards the least recently used items first.
 *
 * This feature is used by the {@link Session} to store the result of
 * cacheable actions ({@link Action.isCacheable}).
 */
class LruCache{

  /**
   * Creates an LruCache
   *
   * @param {number} size - Sets in bytes the maximum size of the cache
   * @param {number} lifespan - Lifespan in milliseconds about amount of time that
   * an item under LRU cache should be kept alive
   */
  constructor(size, lifespan){

    assert(TypeCheck.isNumber(size), 'size requires a number value');
    assert(TypeCheck.isNumber(lifespan), 'lifespan requires a number value');

    this[_size] = size;
    this[_lifespan] = lifespan;

    // lazy creation
    this[_cache] = null;
  }

  /**
   * Returns the size of the cache in bytes
   *
   * @type {number}
   */
  get size(){
    return this[_size];
  }

  /**
   * Returns the lifespan for the values held by the cache in seconds
   *
   * @type {number}
   */
  get lifespan(){
    return this[_lifespan];
  }

  /**
   * Resets the cache by flushing it
   *
   * @param {string} [key] - id associated with the value otherwise flushes the whole
   * cache
   */
  flush(key=null){

    if (this[_cache] === null){
      return;
    }

    if (key){
      this[_cache].del(key);
    }
    else{
      this[_cache].reset();
    }
  }

  /**
   * Returns a list containing the keys of the values held by the cache
   *
   * @return {Array<string>}
   */
  keys(){
    return (this[_cache] === null) ? [] : this[_cache].keys();
  }

  /**
   * Returns the number of items held by the cache
   *
   * @type {number}
   */
  get length(){
    return this.keys().length;
  }

  /**
   * Returns a boolean telling if the input key is under the cache
   *
   * @param {string} key - id associated with the value
   * @return {boolean}
   */
  has(key){
    return (this[_cache] !== null && this[_cache].has(key));
  }

  /**
   * Returns the value from the lru cache based on the input {@link Action.id},
   * otherwise if the id is not found under the cache then returns undefined
   *
   * @param {string} key - id associated with the value
   * @param {*} [defaultValue=undefined] - value returned in case the key does not exist
   * @return {*}
   */
  get(key, defaultValue=undefined){
    assert(TypeCheck.isString(key), 'Invalid action key!');

    return (this.has(key)) ? this[_cache].get(key).data : defaultValue;
  }

  /**
   * Sets a result value to the lru cache, this value is associated with the {@link Action.id}
   *
   * @param {string} key - id associated with the value
   * @param {*} value - value that should be store by the cache
   */
  set(key, value){
    assert(TypeCheck.isString(key), 'Invalid action key!');

    if (this[_cache] === null){
      this[_cache] = new LruCacheBase({
        max: this[_size],
        maxAge: this[_lifespan],
        length: (n, k) => {
          return n.memorySize;
        },
      });
    }

    const resultHolder = Object.create(null);
    resultHolder.memorySize = sizeof(value);
    resultHolder.data = value;

    this[_cache].set(key, resultHolder);
  }
}

module.exports = LruCache;

const assert = require('assert');
const TypeCheck = require('js-typecheck');


/**
 * Merges two plain objects recursively and returns a new one
 *
 * @param {Object} objectA - plain object a
 * @param {Object} objectB - plain object b
 * @return {Object}
 * @private
 */
function deepMerge(objectA, objectB){

  assert(TypeCheck.isPlainObject(objectA), 'objectA needs to be a plain object');
  assert(TypeCheck.isPlainObject(objectB), 'objectB needs to be a plain object');

  const result = Object.assign({}, objectA);

  for (const key in objectB){

    // merging objects
    if (TypeCheck.isPlainObject(objectB[key]) && key in objectA){
      result[key] = deepMerge(objectA[key], objectB[key]);
    }
    // overriding / assigning
    else{
      result[key] = objectB[key];
    }
  }

  return result;
}

module.exports = deepMerge;

